import React from 'react'


function TextInput({ label, placeholder, name, rows }) {
    return (
      <div>
        <label htmlFor={name}>{label}</label>
        <textarea
          id={name}
          name={name}
          placeholder={placeholder}
          rows={rows}
          style={{ border:"none",
            outline:"none",
            width:"100%",
            color:'inherit',
            fontSize:"16px",
            background:"transparent",
           }}
        ></textarea>
      </div>
    );
  }

export default TextInput
