import React,{ useEffect, useState } from 'react';
import styled from "styled-components";
import ImageCard from '../components/imageCard';
import SearchBar from '../components/SearchBar';
import { CircularProgress } from '@mui/material';
import { GetPosts } from '../api';
const Container = styled.div`
  height:100%;
  overflow-y:scroll;
  backgroung:${({theme})=>theme.bg};
  padding:30px 30px;
  padding-bottom:50px;
  display:flex;
  flex-direction:column;
  align-items:center;
  gap:20px;
  @media (max-width:768px){
  padding: 6px 10px;};

  `;
  const Headline = styled.div`
    font-size:34px;
    font-weight:500;
    color:${({theme})=> theme.text_primary};
    display:flex;
    align-items: center;
    flex-direction: column;
    @media(max-width:768px){
    font-size:22px;};`;
  const Span = styled.div`
    font-size:30px;
    font-weight:500;
    color:${({theme})=> theme.button};
    @media(max-width:768px){
    font-size:20px};`;

  const Wrapper = styled.div`
    width:100%;
    max-width:1400px;
    padding:32px 0px;
    display: flex;
    justify-content:center;
  `;

  const CardWrapper= styled.div`
    display:grid;
    gap:20px;
    @media(min-width:1400px){
      grid-template-columns:repeat(4,1fr);
      };
    @media (min-width:640px) and   (max-width:1399px) {
      grid-template-columns:repeat(3, 1fr);
      };
    @media (max-width:639px) {
      grid-template-columns: repeat(2, 1fr);
      };
      `;

const Home = () => {
  const [posts,setPosts]=useState([]);
  const [loading,setLoading]= useState(false);
  const [error,setError]= useState("");
  const [search,setSearch]=useState("");
  const[filteredPosts,setFilteredPosts]=useState([]);
  
  const getPosts = async () => {
    setLoading(true);
    await GetPosts().then((res) => {
        setLoading(false)
      setPosts(res?.data?.data);
      setFilteredPosts(res?.data?.data)
    })
    .catch((error)=>{
      setError(error?.reponse?.data?.message);
      setLoading(false);
  })
  }
  useEffect(()=>{
    getPosts();
  },[]);

  useEffect(()=>{
    if(!search){
      SearchFilteredPosts(posts);

    }
    const SearchFilteredPosts =posts.filter((post)=>{
      const promptMatch= post?.prompt?.toLowerCase().includes(search.to);
      const authorMatch = post?.name?.toLowerCase().includes(search);
      return promptMatch || authorMatch;
    });

    if(search){
      setFilteredPosts(SearchFilteredPosts)
    }
  },[posts,search])
  return (
    <Container>
      <Headline>
        Explore popular posts in the Comunity!
        <Span>☉ Generated with AI ☉</Span>
      </Headline>
      <SearchBar search={search} setSearch={setSearch}/>
      <Wrapper>
        {error && <div style ={{ color:"red"}}>{error}</div>}
        {loading ?(
         <CircularProgress/>
        ):(

        <CardWrapper>
          {filteredPosts.length=== 0 ?<>No Posts Found</>:
          <>
          {filteredPosts
            .slice()
            .reverse()
            .map((item,index)=>{
              <ImageCard key={index} item={item} />
            })}
          </>}
       
        
        
       
        
        </CardWrapper>)}
      </Wrapper>
    </Container>
  )
}

export default Home
