import React from 'react'
import styled from "styled-components"
import Button from "./button"
import { AutoAwesome, CreateRounded } from '@mui/icons-material';

const From= styled.div`
    flex:1;
    padding: 16px 20px;
    display:flex;
    flex-direction:column;
    gap: 9%;
    justify-content:center;
    `;
const Top= styled.div`flex:1;
    flex-direction: column;
    gap: 6px;`;
const Title= styled.div`
    font-size: 28px;
    font-weight:500;
    color: ${({theme})=> theme.text_primary};`;
const Desc= styled.div`font-size: 17px;
    font-weight:400;
    color: ${({theme})=> theme.text_secondary}`;  
const Body= styled.div`
    display: flex;
    flex-direction: column;
    gap: 18px;
    font-size:12px;
    font-weight:400;
    color:${({theme})=> theme.text_secondary};
    `;
const Actions= styled.div`
    flex: 1;
    display: flex;
    gap:8px;
    `;
const GenerateImageForm = () => {
  return (
    <From>
        <Top>
            <Title>Generate Image with prompt</Title>
                
            <Desc>Write your promt according to the image you want </Desc>
        </Top>
        <Body>{

function TextInput({ label, placeholder, name, rows }) {
  return (
    <div>
      <label htmlFor={name}>{label}</label>
      <textarea
        id={name}
        name={name}
        placeholder={placeholder}
        rows={rows}
        style={{ width: "100%", marginTop: "5px" }}
      ></textarea>
    </div>
  );
}





}</Body>
        <Actions><Button text="Generate Image" flex leftIcon={<AutoAwesome></AutoAwesome>}/>
        <Button text="Post Image" flex type="secondary" leftIcon={<CreateRounded/>}/></Actions>
    </From>
  )
}

export default GenerateImageForm
