import React from 'react'
import styled from 'styled-components'


const SearchBarContainer=styled.div`
    max-width:550px;
    display:flex;
    flex-direction:column;
    width:100%;
    border:1px solid ${({theme})=> theme.text_secondary + 90};
    color:${({theme})=> theme.text_primary  };
    border-radius:8px;
    padding:8px
    cursor: pointer;
    gap: 6px;
    align-items:center;
`

const SearchBar = ({label,placeholder,name,rows}) => {
  return (<>
   
  <SearchBarContainer>
     <label >{label}</label> 
    
    <textarea 
    id={name}
    name={name}
    placeholder={placeholder}
    rows={rows}
     style={{
        border:"none",
        outline:"none",
        width:"100%",
        color:'inherit',
        fontSize:"16px",
        background:"transparent",
        
    }}/>
  </SearchBarContainer>
  </>
  )
}

export default SearchBar;
